mapper.insert:

insert into stock_base (code, `name`, circulating_stock_capital, 
  circulating_stock_value, all_stock_capital, 
  all_stock_value, per, pbr
  )
values (#{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{circulatingStockCapital,jdbcType=DECIMAL}, 
  #{circulatingStockValue,jdbcType=DECIMAL}, #{allStockCapital,jdbcType=DECIMAL}, 
  #{allStockValue,jdbcType=DECIMAL}, #{per,jdbcType=DECIMAL}, #{pbr,jdbcType=DECIMAL}
  )
