mapper.select:

select 

from stock_base
where code = #{code,jdbcType=VARCHAR}
  and `date` = #{date,jdbcType=VARCHAR}

mapper.delete:

delete from stock_base
where code = #{code,jdbcType=VARCHAR}
  and `date` = #{date,jdbcType=VARCHAR}

mapper.update:

update stock_base
set `name` = #{name,jdbcType=VARCHAR},
  circulating_stock_capital = #{circulatingStockCapital,jdbcType=INTEGER},
  circulating_stock_value = #{circulatingStockValue,jdbcType=DECIMAL},
  all_stock_capital = #{allStockCapital,jdbcType=INTEGER},
  all_stock_value = #{allStockValue,jdbcType=DECIMAL},
  per = #{per,jdbcType=DECIMAL},
  pbr = #{pbr,jdbcType=DECIMAL}
where code = #{code,jdbcType=VARCHAR}
  and `date` = #{date,jdbcType=VARCHAR}
