mapper.select:
select

from stock_excel_template


        and id=#{id,jdbcType=VARCHAR}


        and rise_Id=#{riseId,jdbcType=VARCHAR}


        and fail_Id=#{failId,jdbcType=VARCHAR}


        and limit_Up_Id=#{limitUpId,jdbcType=VARCHAR}


        and limit_Up_one_Id=#{limitUpOneId,jdbcType=VARCHAR}


        and limit_Up_two_Id=#{limitUpTwoId,jdbcType=VARCHAR}


        and limit_Up_three_Id=#{limitUpThreeId,jdbcType=VARCHAR}


        and remark=#{remark,jdbcType=VARCHAR}


        and order_str=#{orderStr,jdbcType=VARCHAR}


        and order_by=#{orderBy,jdbcType=VARCHAR}
